%  Test fragment script for frozen group delay functionfftl=1024;fftl2=fftl/2;cornf=1000;gdbw=70;delsp=1;rbb2=fftl/2:-1:2;t=([1:fftl]-fftl/2-1)/fftl*2;adjd=1.0./(1+exp(-20*t)); % correction function for smooth transition at fs/2gw=exp(-0.25*pi*(fs*(t/2)/gdbw).^2); % slope difinition functiongw=gw/sum(gw);            % gdbw is the equvalent rectangular band widthfgw=real(fft(fftshift(gw))); % gw is the spectral smoothing windowdf=fs/fftl*2*pi;    % normalization constant for integration and differentiationfw=(1:fftl2+1)/fftl*fs; % frequency axistrbw=300;		% width of transition arearho=1.0./(1+exp(-(fw-cornf)/trbw)); % rondom group delay weighting functionnz=randn(1,fftl2+1).*rho; %((rho*0+1)*mixNlow(round(ii))+(1-mixNlow(round(ii)))*rho);nz=real(ifft(fft([nz,nz(rbb2)]).*fgw));nz=nz*sqrt(fftl*gdbw/fs);   % correction factor for noisenz=nz*delsp*df/1000;mz=cumsum([nz(1:fftl2+1),nz(rbb2)])-nz(1);mmz=-(mz-adjd*(rem((mz(fftl)+mz(2)),2*pi)-2*pi));pz=exp(-i*mmz)'; %.*[wlcut wlcut(rbb2)]';